mapper.select:
select (CASE SUBSTR(gb.ACC_CODE, 1, 1) WHEN '4' THEN sum( gb.CR_AMT ) - sum(gb.DR_AMT) WHEN '6' THEN sum( gb.CR_AMT ) - sum(gb.DR_AMT) WHEN '5' THEN sum(gb.DR_AMT) - sum( gb.CR_AMT) WHEN '7' THEN sum(gb.DR_AMT) - sum( gb.CR_AMT) ELSE sum( gb.CR_AMT) END) AS "crAmt", gb.ACC_CODE as "accCode" from
a6.GL_BAL gb
gb.ACCOUNT_ID = '001' AND gb.VOU_KIND != 'JZ'
and ACC_CODE = #{accCode}
and gb.co_code Like #{deptId}
AND gb.FISCAL|| '-' || lpad(gb.FIS_PERD,2,'0') >= #{beginTime}
AND gb.FISCAL|| '-' || lpad(gb.FIS_PERD,2,'0') <= #{endTime}
group by gb.ACC_CODE

select sum(a.cr_amt) "crAmt",a.co_code "coCode" from ( select (CASE SUBSTR(gb.ACC_CODE, 1, 1) WHEN '4' THEN sum( gb.CR_AMT ) - sum(gb.DR_AMT) WHEN '6' THEN sum( gb.CR_AMT ) - sum(gb.DR_AMT) WHEN '5' THEN sum(gb.DR_AMT) - sum( gb.CR_AMT) WHEN '7' THEN sum(gb.DR_AMT) - sum( gb.CR_AMT) ELSE sum( gb.CR_AMT) END) AS cr_amt, gb.ACC_CODE as acc_code, gb.co_code as co_code from
a6.GL_BAL gb
gb.ACCOUNT_ID = '001' AND gb.VOU_KIND != 'JZ'
and ACC_CODE like #{accCode}
and gb.co_code Like #{deptId}
AND gb.FISCAL|| '-' || lpad(gb.FIS_PERD,2,'0') >= #{beginTime}
AND gb.FISCAL|| '-' || lpad(gb.FIS_PERD,2,'0') <= #{endTime}
group by gb.co_code,gb.ACC_CODE) a group by a.co_code
