select (CASE SUBSTR(gb.ACC_CODE, 1, 1) WHEN '4' THEN sum( gb.CR_AMT ) - sum(gb.DR_AMT) WHEN '6' THEN sum( gb.CR_AMT ) - sum(gb.DR_AMT) WHEN '5' THEN sum(gb.DR_AMT) - sum( gb.CR_AMT) WHEN '7' THEN sum(gb.DR_AMT) - sum( gb.CR_AMT) ELSE sum( gb.CR_AMT) END) AS "crAmt", gb.B_ACC_CODE as "bAccCode",gb.ACC_CODE as "accCode" from
a6.GL_BAL gb
gb.B_ACC_CODE != '*' AND gb.ACCOUNT_ID = '001' AND gb.VOU_KIND != 'JZ'
and B_ACC_CODE LIKE #{bAccCode}
and gb.co_code Like #{deptId}
AND gb.FISCAL|| '-' || lpad(gb.FIS_PERD,2,'0') >= #{beginTime}
AND gb.FISCAL|| '-' || lpad(gb.FIS_PERD,2,'0') <= #{endTime}
group by gb.B_ACC_CODE, gb.ACC_CODE

