SELECT gb.B_ACC_CODE AS "bAccCode", gb.ITEM_CODE AS "itemCode" FROM
a6.GL_BAL gb
gb.ACCOUNT_ID = '001'AND gb.VOU_KIND != 'JZ'
and gb.co_code Like #{deptId}
AND gb.FISCAL|| '-' || lpad(gb.FIS_PERD,2,'0') >= #{beginTime}
AND gb.FISCAL|| '-' || lpad(gb.FIS_PERD,2,'0') <= #{endTime}
GROUP BY gb.ITEM_CODE, gb.B_ACC_CODE ORDER BY gb.ITEM_CODE, gb.B_ACC_CODE

select (CASE SUBSTR(gb.ACC_CODE, 1, 1) WHEN '4' THEN sum( gb.CR_AMT ) - sum(gb.DR_AMT) WHEN '6' THEN sum( gb.CR_AMT ) - sum(gb.DR_AMT) WHEN '5' THEN sum(gb.DR_AMT) - sum( gb.CR_AMT) WHEN '7' THEN sum(gb.DR_AMT) - sum( gb.CR_AMT) ELSE sum( gb.CR_AMT) END) AS "amt", gb.ACC_CODE AS "accCode" from
a6.GL_BAL gb
where gb.ACCOUNT_ID = '001' AND gb.VOU_KIND != 'JZ'
and gb.ACC_CODE LIKE #{accCode}
and gb.co_code Like #{deptId}
