group by gb.ACC_CODE ,gb.acc_item1,gb.acc_item2, gb.acc_item3,gb.acc_item4,gb.acc_item5,gb.acc_item6, gb.acc_item7,gb.acc_item8,gb.acc_item9,gb.acc_item10,gb.acc_item11, gb.acc_item12, gb.acc_item13,gb.acc_item14,gb.acc_item15,gb.acc_item16
