mapper.select:
select (CASE SUBSTR(gb.ACC_CODE, 1, 1) WHEN '4' THEN sum( gb.CR_AMT ) - sum(gb.DR_AMT) WHEN '6' THEN sum( gb.CR_AMT ) - sum(gb.DR_AMT) WHEN '5' THEN sum(gb.DR_AMT) - sum( gb.CR_AMT) WHEN '7' THEN sum(gb.DR_AMT) - sum( gb.CR_AMT) END) AS "caAmt", gb.B_ACC_CODE as "bAccCode",gb.ACC_CODE as "accCode" from
GL_BAL gb
gb.B_ACC_CODE != '*' and gb.ACCOUNT_ID = '001' AND gb.VOU_KIND != 'JZ'
and B_ACC_CODE = #{bAccCode}
and ACC_CODE = #{accCode}
and gb.co_code Like #{deptId}
AND gb.FISCAL|| '-' || lpad(gb.FIS_PERD,2,'0') >= #{beginTime}
AND gb.FISCAL|| '-' || lpad(gb.FIS_PERD,2,'0') <= #{endTime}
group by gb.B_ACC_CODE, gb.ACC_CODE

select t.B_ACC_CODE as "bAccCode" , bac.B_ACC_NAME as "bAccName" from (select distinct(gb.B_ACC_CODE) from
GL_BAL gb
where gb.B_ACC_CODE != '*' and gb.ACCOUNT_ID = '001' AND gb.VOU_KIND != 'JZ'
and gb.co_code Like #{deptId}
) t LEFT JOIN MA_BACC bac ON t.B_ACC_CODE = bac.B_ACC_CODE GROUP BY t.B_ACC_CODE ,bac.B_ACC_NAME order by t.B_ACC_CODE
